import React, { useEffect, useState } from "react";
import axios from "axios";
import EditProfileForm from "../components/profile/EditProfileForm"; // ch·ªânh ƒë∆∞·ªùng d·∫´n cho ƒë√∫ng

const ProfilePage = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);

  // üîÑ H√†m load danh s√°ch ng∆∞·ªùi d√πng
  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users");
      setUsers(res.data);
    } catch (err) {
      console.error("L·ªói khi t·∫£i danh s√°ch:", err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // üóëÔ∏è X√≥a ng∆∞·ªùi d√πng
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ng∆∞·ªùi d√πng n√†y kh√¥ng?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      fetchUsers();
    } catch (err) {
      console.error("L·ªói khi x√≥a:", err);
    }
  };

  // üíæ L∆∞u ch·ªânh s·ª≠a
  const handleSave = async (userId, updatedData) => {
    try {
      await axios.put(`http://localhost:5000/api/users/${userId}`, updatedData);
      alert("C·∫≠p nh·∫≠t th√†nh c√¥ng!");
      setEditingUser(null);
      fetchUsers();
    } catch (err) {
      console.error("L·ªói khi l∆∞u:", err);
      alert("L∆∞u th·∫•t b·∫°i!");
    }
  };

  return (
    <div className="profile-page-container">
      <h1>Danh s√°ch ng∆∞·ªùi d√πng</h1>

      <table className="user-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>T√™n</th>
            <th>Email</th>
            <th>Ph√¢n quy·ªÅn</th>
            <th>H√†nh ƒë·ªông</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u) => (
            <React.Fragment key={u._id}>
              <tr>
                <td>{u._id}</td>
                <td>{u.name}</td>
                <td>{u.email}</td>

                {/* ‚úÖ C·ªôt ph√¢n quy·ªÅn */}
                <td>
                  <span
                    style={{
                      backgroundColor:
                        u.role === "admin" ? "#DBEAFE" : "#F3F4F6",
                      color: u.role === "admin" ? "#1E40AF" : "#374151",
                      padding: "4px 10px",
                      borderRadius: "12px",
                      fontSize: "13px",
                      fontWeight: 500,
                    }}
                  >
                    {u.role === "admin" ? "Admin" : "User"}
                  </span>
                </td>

                <td>
                  {editingUser && editingUser._id === u._id ? (
                    <>
                      <button
                        className="action-btn small cancel"
                        onClick={() => setEditingUser(null)}
                      >
                        H·ªßy
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        className="action-btn edit small"
                        onClick={() => setEditingUser(u)}
                      >
                        S·ª≠a
                      </button>
                      <button
                        className="action-btn delete small"
                        onClick={() => handleDelete(u._id)}
                      >
                        X√≥a
                      </button>
                    </>
                  )}
                </td>
              </tr>

              {/* ‚úÖ Form ch·ªânh s·ª≠a hi·ªÉn th·ªã ngay b√™n d∆∞·ªõi */}
              {editingUser && editingUser._id === u._id && (
                <tr>
                  <td colSpan="5">
                    <EditProfileForm
                      user={editingUser}
                      onUpdated={() => {
                        setEditingUser(null);
                        fetchUsers(); // reload danh s√°ch
                      }}
                    />
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProfilePage;
