import React, { useState } from "react";
import api from "../../services/api";
import { useNavigate, Link } from "react-router-dom";

export default function RegisterForm() {
  const [form, setForm] = useState({ name: "", email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await api.post("/auth/signup", form);
      alert(res.data.message || "Đăng ký thành công");
      navigate("/login");
    } catch (err) {
      alert(err.response?.data?.message || err.message || "Đăng ký thất bại");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="form-card">
      <h2>Đăng ký</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" required placeholder="Tên" value={form.name} onChange={handleChange} />
        <input name="email" required type="email" placeholder="Email" value={form.email} onChange={handleChange} />
        <input name="password" required type="password" placeholder="Mật khẩu" value={form.password} onChange={handleChange} />
        <button type="submit" disabled={loading}>{loading ? "Đang xử lý..." : "Đăng ký"}</button>
      </form>
      <p>Đã có tài khoản? <Link to="/login">Đăng nhập</Link></p>
    </div>
  );
}
