import React, { useContext, useState } from "react";
import { AuthContext } from "../../contexts/AuthContext";
import api from "../services/api";

export default function ProfilePage() {
  const { user, setUser, logout } = useContext(AuthContext);
  const [form, setForm] = useState({ name: user?.name || "", password: "" });
  const [loading, setLoading] = useState(false);

  const handleUpdate = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await api.put("/profile", form);
      // cập nhật user ở client: gọi lại profile
      const { data } = await api.get("/profile");
      setUser(data);
      alert("Cập nhật thành công");
      setForm(prev => ({ ...prev, password: "" }));
    } catch (err) {
      alert(err.response?.data?.message || "Cập nhật thất bại");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  return (
    <div className="profile-card">
      <h2>Thông tin cá nhân</h2>
      <p><strong>Email:</strong> {user?.email}</p>
      <form onSubmit={handleUpdate}>
        <input name="name" value={form.name} onChange={handleChange} placeholder="Tên hiển thị" />
        <input name="password" type="password" value={form.password} onChange={handleChange} placeholder="Mật khẩu mới (để trống nếu không đổi)" />
        <button type="submit" disabled={loading}>{loading ? "Đang cập nhật..." : "Cập nhật"}</button>
      </form>
      <button onClick={logout}>Đăng xuất</button>
    </div>
  );
}
