import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({ name: "", email: "" });

  // üì• L·∫•y danh s√°ch ng∆∞·ªùi d√πng t·ª´ backend
  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users");
      setUsers(res.data);
    } catch (err) {
      console.error("L·ªói khi t·∫£i ng∆∞·ªùi d√πng:", err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // ‚ûï Th√™m ng∆∞·ªùi d√πng
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name || !form.email) return;
    try {
      await axios.post("http://localhost:5000/api/users", form);
      setForm({ name: "", email: "" });
      fetchUsers();
    } catch (err) {
      console.error("L·ªói khi th√™m ng∆∞·ªùi d√πng:", err);
    }
  };

  // üóëÔ∏è X√≥a ng∆∞·ªùi d√πng
  const handleDelete = async (id) => {
    if (window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ng∆∞·ªùi d√πng n√†y kh√¥ng?")) {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      fetchUsers();
    }
  };

  return (
    <div className="App" style={{ backgroundColor: "#e9ecff", minHeight: "100vh", padding: "40px" }}>
      <h2>Qu·∫£n l√Ω ng∆∞·ªùi d√πng (MongoDB + React)</h2>

      {/* Form th√™m ng∆∞·ªùi d√πng */}
      <div style={{
        background: "white",
        borderRadius: "12px",
        padding: "20px",
        width: "400px",
        margin: "20px auto",
        boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
      }}>
        <h3>Th√™m ng∆∞·ªùi d√πng m·ªõi</h3>
        <form onSubmit={handleSubmit}>
          <div>
            <label>T√™n:</label><br />
            <input
              type="text"
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              required
            />
          </div>
          <div style={{ marginTop: "10px" }}>
            <label>Email:</label><br />
            <input
              type="email"
              value={form.email}
              onChange={(e) => setForm({ ...form, email: e.target.value })}
              required
            />
          </div>
          <button type="submit" style={{
            marginTop: "15px",
            padding: "8px 16px",
            backgroundColor: "#4e54c8",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer"
          }}>+ Th√™m</button>
        </form>
      </div>

      {/* B·∫£ng danh s√°ch ng∆∞·ªùi d√πng */}
      <div style={{
        background: "white",
        borderRadius: "12px",
        padding: "20px",
        width: "80%",
        margin: "auto",
        boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
      }}>
        <h3>üìã Danh s√°ch ng∆∞·ªùi d√πng</h3>
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ background: "#4e54c8", color: "white" }}>
              <th style={{ padding: "10px" }}>ID</th>
              <th style={{ padding: "10px" }}>T√™n</th>
              <th style={{ padding: "10px" }}>Email</th>
              <th style={{ padding: "10px" }}>H√†nh ƒë·ªông</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((u) => (
                <tr key={u._id} style={{ borderBottom: "1px solid #ddd" }}>
                  <td>{u._id}</td>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td>
                    <button
                      onClick={() => handleDelete(u._id)}
                      style={{
                        background: "#e63946",
                        color: "white",
                        border: "none",
                        borderRadius: "5px",
                        padding: "5px 10px",
                        cursor: "pointer"
                      }}
                    >
                      X√≥a
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" style={{ textAlign: "center", padding: "10px" }}>
                  Ch∆∞a c√≥ ng∆∞·ªùi d√πng n√†o üòÖ
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
