import React, { useEffect, useState } from "react";
import api from "../../services/api";
import { useAuth } from "../../contexts/AuthContext";
import { useToast } from "../../contexts/ToastContext";

export default function AdminUserList() {
  const { user: me, logout } = useAuth(); // ‚úÖ Th√™m logout
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState({ name: "", email: "" });
  const [query, setQuery] = useState("");
  const { addToast } = useToast();

  // ‚úÖ L·ªçc & s·∫Øp x·∫øp (admin l√™n ƒë·∫ßu)
  const filtered = users
    .filter((u) => {
      if (!query) return true;
      const q = query.toLowerCase();
      return (
        (u.name || "").toLowerCase().includes(q) ||
        (u.email || "").toLowerCase().includes(q)
      );
    })
    .sort((a, b) => {
      // Admin l√™n tr∆∞·ªõc
      if (a.role === "admin" && b.role !== "admin") return -1;
      if (a.role !== "admin" && b.role === "admin") return 1;
      return 0;
    });

  const fetchUsers = async () => {
    try {
      const res = await api.get("/users");
      setUsers(res.data);
    } catch (err) {
      console.error("Failed to fetch users:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async (id) => {
    if (!confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a user n√†y?")) return;
    try {
      await api.delete(`/users/${id}`);
      setUsers((s) => s.filter((u) => u._id !== id));
      addToast("ƒê√£ x√≥a user", "success");
    } catch (err) {
      console.error(err);
      addToast(err.response?.data?.message || "X√≥a th·∫•t b·∫°i", "error");
    }
  };

  const startEdit = (u) => {
    setEditingId(u._id);
    setEditData({ name: u.name, email: u.email });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditData({ name: "", email: "" });
  };

  const saveEdit = async (id) => {
    try {
      const res = await api.put(`/users/${id}`, editData);
      setUsers((s) => s.map((u) => (u._id === id ? res.data.user : u)));
      cancelEdit();
      addToast("C·∫≠p nh·∫≠t th√†nh c√¥ng", "success");
    } catch (err) {
      console.error(err);
      addToast(err.response?.data?.message || "C·∫≠p nh·∫≠t th·∫•t b·∫°i", "error");
    }
  };

  const handleLogout = () => {
    logout();
    addToast("ƒê√£ ƒëƒÉng xu·∫•t", "success");
  };

  return (
    <div className="app-container">
      {/* --- Header --- */}
      <div className="site-header">
        <h1 className="site-title">üëë Trang qu·∫£n tr·ªã</h1>
        <div style={{ textAlign: "right" }}>
          <div style={{ fontSize: 14, fontWeight: 700 }}>
            {me?.name || "Admin"}
          </div>
          <div style={{ fontSize: 12, color: "#6b7280" }}>{me?.email}</div>
          <div
            style={{
              marginTop: 8,
              display: "flex",
              gap: 8,
              justifyContent: "flex-end",
            }}
          >
            <a
              href="/admin/add"
              className="btn secondary"
              style={{ textDecoration: "none", padding: "6px 10px" }}
            >
              ‚ûï Th√™m user
            </a>

            {/* ‚úÖ N√∫t ƒëƒÉng xu·∫•t */}
            <button
              className="btn danger"
              onClick={handleLogout}
              style={{
                padding: "6px 10px",
                backgroundColor: "#dc2626",
                color: "white",
                border: "none",
                borderRadius: 6,
                cursor: "pointer",
              }}
            >
              ƒêƒÉng xu·∫•t
            </button>
          </div>
        </div>
      </div>

      {/* --- N·ªôi dung b·∫£ng --- */}
      <div className="card">
        <div className="admin-toolbar">
          <h3>Danh s√°ch ng∆∞·ªùi d√πng</h3>
          <div className="admin-actions">
            <input
              className="search-input"
              placeholder="T√¨m theo t√™n ho·∫∑c email"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />
          </div>
        </div>

        {loading ? (
          <p className="muted">ƒêang t·∫£i...</p>
        ) : (
          <div className="table-responsive">
            <table className="user-table">
              <thead>
                <tr>
                  <th style={{ width: 220 }}>ID</th>
                  <th>T√™n</th>
                  <th>Email</th>
                  <th style={{ width: 220 }}>H√†nh ƒë·ªông</th>
                </tr>
              </thead>
              <tbody>
                {filtered.map((u) => (
                  <tr key={u._id}>
                    <td style={{ wordBreak: "break-all" }}>{u._id}</td>

                    {/* --- C·ªôt t√™n --- */}
                    <td>
                      {editingId === u._id ? (
                        <input
                          type="text"
                          value={editData.name}
                          onChange={(e) =>
                            setEditData({ ...editData, name: e.target.value })
                          }
                        />
                      ) : (
                        <div style={{ display: "flex", alignItems: "center" }}>
                          <div className="avatar">
                            {(u.name || "U")
                              .split(" ")
                              .map((s) => s[0])
                              .slice(0, 2)
                              .join("")
                              .toUpperCase()}
                          </div>
                          <div>
                            <div style={{ fontWeight: 700 }}>
                              {u.name}
                              {u.role === "admin" && (
                                <span
                                  style={{
                                    marginLeft: 6,
                                    color: "#dc2626",
                                    fontSize: 12,
                                  }}
                                >
                                  üëë
                                </span>
                              )}
                            </div>
                            <div
                              style={{ fontSize: 12, color: "var(--muted)" }}
                            >
                              {u.role}
                            </div>
                          </div>
                        </div>
                      )}
                    </td>

                    {/* --- C·ªôt email --- */}
                    <td>
                      {editingId === u._id ? (
                        <input
                          type="text"
                          value={editData.email}
                          onChange={(e) =>
                            setEditData({ ...editData, email: e.target.value })
                          }
                        />
                      ) : (
                        u.email
                      )}
                    </td>

                    {/* --- C·ªôt h√†nh ƒë·ªông --- */}
                    <td>
                      {editingId === u._id ? (
                        <>
                          <button
                            className="action-btn edit small"
                            onClick={() => saveEdit(u._id)}
                            style={{ marginRight: 8 }}
                          >
                            L∆∞u
                          </button>
                          <button
                            className="action-btn small"
                            onClick={cancelEdit}
                          >
                            H·ªßy
                          </button>
                        </>
                      ) : (
                        <>
                          <button
                            className="action-btn edit small"
                            onClick={() => startEdit(u)}
                            style={{ marginRight: 8 }}
                          >
                            S·ª≠a
                          </button>
                          <button
                            className="action-btn delete small"
                            onClick={() => handleDelete(u._id)}
                          >
                            X√≥a
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}
