import React, { useState, useEffect } from "react";
import api from "../../services/api";
import { useToast } from "../../contexts/ToastContext";
import { useLocation } from "react-router-dom";
import "../../App.css";

export default function ResetPasswordForm() {
  const [token, setToken] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const { addToast } = useToast();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const t = params.get('token');
    if (t) setToken(t);
  }, [location.search]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!token || !password) return addToast('Vui lòng nhập token và mật khẩu mới', 'warning');
    setLoading(true);
    try {
      await api.post('/auth/reset-password', { token, password });
      addToast('Đổi mật khẩu thành công', 'success');
      setToken(''); setPassword('');
    } catch (err) {
      console.error('Reset error', err);
      addToast(err.response?.data?.message || 'Đổi mật khẩu thất bại', 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card">
        <h2 className="auth-title">Đặt lại mật khẩu</h2>
        <p className="auth-sub">Dán token nhận được hoặc dùng link từ email, nhập mật khẩu mới</p>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Reset token</label>
            <input type="text" value={token} onChange={(e)=>setToken(e.target.value)} required />
          </div>
          <div className="form-group">
            <label>Mật khẩu mới</label>
            <input type="password" value={password} onChange={(e)=>setPassword(e.target.value)} required />
          </div>
          <div className="center">
            <button className="btn" type="submit" disabled={loading}>{loading ? 'Đang gửi...' : 'Đổi mật khẩu'}</button>
          </div>
        </form>
      </div>
    </div>
  );
}
