import React, { useState, useEffect } from "react";
import api from "../../services/api";
import { useAuth } from "../../contexts/AuthContext";

export default function ProfilePage() {
  const { user } = useAuth();
  const [profile, setProfile] = useState({ name: "", email: "" });
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (user?._id) {
      api
        .get(`/profile?id=${user._id}`)
        .then((res) => setProfile(res.data))
        .catch((err) => console.error("L·ªói t·∫£i profile:", err));
    }
  }, [user]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await api.put("/profile", {
        id: user._id,
        name: profile.name,
        email: profile.email,
      });
      setProfile(res.data);
      setMessage("‚úÖ C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng!");
    } catch (err) {
      setMessage("‚ùå L·ªói khi c·∫≠p nh·∫≠t th√¥ng tin!");
      console.error("L·ªói c·∫≠p nh·∫≠t:", err);
    }
  };

  if (!user) return <p>ƒêang t·∫£i...</p>;

  return (
    <div className="profile-container">
      <h2>üë§ Th√¥ng tin c√° nh√¢n</h2>
      <form onSubmit={handleUpdate}>
        <div>
          <label>T√™n:</label>
          <input
            type="text"
            value={profile.name || ""}
            onChange={(e) => setProfile({ ...profile, name: e.target.value })}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={profile.email || ""}
            onChange={(e) => setProfile({ ...profile, email: e.target.value })}
          />
        </div>
        <button type="submit">C·∫≠p nh·∫≠t</button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}
