import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../../contexts/AuthContext";
import { useNavigate } from "react-router-dom";

export default function LoginForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // 沐･ ﾄ雪ｺ｣m b蘯｣o ﾄ妥ｺng URL backend
      const res = await axios.post("http://localhost:5000/api/users/login", {
        email,
        password,
      });

      const { token, user } = res.data;
      login(token, user); // g盻絞 hﾃm t盻ｫ AuthContext
      setMessage("ﾄ斉ハg nh蘯ｭp thﾃnh cﾃｴng!");
      navigate("/profile");
    } catch (err) {
      console.error("L盻擁 ﾄ惰ハg nh蘯ｭp:", err);
      setMessage(err.response?.data?.message || "ﾄ斉ハg nh蘯ｭp th蘯･t b蘯｡i!");
    }
  };

  return (
    <div className="auth-container">
      <h2>ﾄ斉ハg nh蘯ｭp</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="M蘯ｭt kh蘯ｩu"
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">ﾄ斉ハg nh蘯ｭp</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}
