import React, { useState, useEffect } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { useToast } from "../../contexts/ToastContext";
import api from "../../services/api";
import "../../App.css";

export default function ResetPasswordForm() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const { addToast } = useToast();

  const [token, setToken] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  // L·∫•y token t·ª´ URL query params
  useEffect(() => {
    const tokenFromUrl = searchParams.get("token");
    if (!tokenFromUrl) {
      addToast("‚ö†Ô∏è Link ƒë·∫∑t l·∫°i m·∫≠t kh·∫©u kh√¥ng h·ª£p l·ªá ho·∫∑c ƒë√£ h·∫øt h·∫°n", "warning");
      setTimeout(() => navigate("/login"), 2000);
    } else {
      setToken(tokenFromUrl);
    }
  }, [searchParams, navigate, addToast]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (!password || !confirmPassword) {
      addToast("‚ö†Ô∏è Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin", "warning");
      return;
    }

    if (password.length < 6) {
      addToast("‚ùå M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±", "error");
      return;
    }

    if (password !== confirmPassword) {
      addToast("‚ùå M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp", "error");
      return;
    }

    setLoading(true);
    try {
      const response = await api.post("/auth/reset-password", {
        token,
        password,
      });

      setSuccess(true);
      addToast("‚úÖ " + (response.data.message || "ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!"), "success");

      // Redirect to login after 2 seconds
      setTimeout(() => {
        navigate("/login");
      }, 2000);
    } catch (err) {
      console.error("Reset password error:", err);
      addToast(
        err.response?.data?.message || "‚ùå L·ªói khi ƒë·∫∑t l·∫°i m·∫≠t kh·∫©u",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  if (success) {
    return (
      <div style={styles.container}>
        <div style={styles.card}>
          <div style={styles.successIcon}>‚úÖ</div>
          <h2 style={styles.title}>Th√†nh C√¥ng!</h2>
          <p style={styles.description}>
            M·∫≠t kh·∫©u c·ªßa b·∫°n ƒë√£ ƒë∆∞·ª£c ƒë·∫∑t l·∫°i th√†nh c√¥ng.
          </p>
          <p style={styles.hint}>ƒêang chuy·ªÉn ƒë·∫øn trang ƒëƒÉng nh·∫≠p...</p>
          <a href="/login" className="btn" style={{ display: "block", textAlign: "center" }}>
            ƒêƒÉng nh·∫≠p ngay
          </a>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.iconWrapper}>
          <div style={styles.icon}>üîë</div>
        </div>

        <h2 style={styles.title}>ƒê·∫∑t L·∫°i M·∫≠t Kh·∫©u</h2>
        <p style={styles.description}>
          Nh·∫≠p m·∫≠t kh·∫©u m·ªõi c·ªßa b·∫°n b√™n d∆∞·ªõi.
        </p>

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>M·∫≠t kh·∫©u m·ªõi</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi (t·ªëi thi·ªÉu 6 k√Ω t·ª±)"
              disabled={loading}
              style={styles.input}
              required
              minLength={6}
            />
            <div style={styles.passwordHint}>
              {password.length > 0 && password.length < 6 && (
                <span style={{ color: "#ef4444" }}>
                  ‚ö†Ô∏è M·∫≠t kh·∫©u qu√° ng·∫Øn (t·ªëi thi·ªÉu 6 k√Ω t·ª±)
                </span>
              )}
              {password.length >= 6 && (
                <span style={{ color: "#10b981" }}>
                  ‚úì ƒê·ªô d√†i h·ª£p l·ªá
                </span>
              )}
            </div>
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
            <input
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Nh·∫≠p l·∫°i m·∫≠t kh·∫©u m·ªõi"
              disabled={loading}
              style={styles.input}
              required
            />
            <div style={styles.passwordHint}>
              {confirmPassword && (
                <span
                  style={{
                    color: password === confirmPassword ? "#10b981" : "#ef4444",
                  }}
                >
                  {password === confirmPassword
                    ? "‚úì M·∫≠t kh·∫©u kh·ªõp"
                    : "‚ö†Ô∏è M·∫≠t kh·∫©u kh√¥ng kh·ªõp"}
                </span>
              )}
            </div>
          </div>

          <button
            type="submit"
            className="btn"
            disabled={loading || password !== confirmPassword || password.length < 6}
            style={{ width: "100%", marginTop: 16 }}
          >
            {loading ? "‚è≥ ƒêang x·ª≠ l√Ω..." : "ÔøΩ ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u"}
          </button>
        </form>

        <div style={styles.footer}>
          <a href="/login" style={styles.link}>
            ‚Üê Quay l·∫°i ƒëƒÉng nh·∫≠p
          </a>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
    padding: "20px",
  },
  card: {
    background: "white",
    borderRadius: "12px",
    boxShadow: "0 10px 40px rgba(0,0,0,0.2)",
    padding: "40px",
    maxWidth: "450px",
    width: "100%",
  },
  iconWrapper: {
    textAlign: "center",
    marginBottom: "20px",
  },
  icon: {
    fontSize: "64px",
    display: "inline-block",
    animation: "pulse 2s infinite",
  },
  successIcon: {
    fontSize: "64px",
    textAlign: "center",
    marginBottom: "20px",
  },
  title: {
    fontSize: "28px",
    fontWeight: "bold",
    color: "#1f2937",
    textAlign: "center",
    marginBottom: "12px",
  },
  description: {
    fontSize: "15px",
    color: "#6b7280",
    textAlign: "center",
    marginBottom: "24px",
    lineHeight: 1.6,
  },
  hint: {
    fontSize: "14px",
    color: "#9ca3af",
    textAlign: "center",
    marginBottom: "24px",
  },
  form: {
    marginBottom: "24px",
  },
  inputGroup: {
    marginBottom: "16px",
  },
  label: {
    display: "block",
    marginBottom: "8px",
    fontSize: "14px",
    fontWeight: "500",
    color: "#374151",
  },
  input: {
    width: "100%",
    padding: "10px 12px",
    borderRadius: "6px",
    border: "1px solid #d1d5db",
    fontSize: "14px",
    transition: "border-color 0.15s ease",
  },
  passwordHint: {
    marginTop: "6px",
    fontSize: "12px",
    minHeight: "18px",
  },
  footer: {
    textAlign: "center",
    marginTop: "16px",
  },
  link: {
    color: "#6b7280",
    textDecoration: "none",
    fontSize: "14px",
    "&:hover": {
      textDecoration: "underline",
    },
  },
};
