import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useToast } from "../../contexts/ToastContext";
import api from "../../services/api";
import "../../App.css";

export default function ForgotPasswordForm() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [sent, setSent] = useState(false);
  const [devToken, setDevToken] = useState(null);
  const { addToast } = useToast();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      addToast("‚ö†Ô∏è Vui l√≤ng nh·∫≠p email", "warning");
      return;
    }

    // Ki·ªÉm tra ƒë·ªãnh d·∫°ng email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      addToast("‚ùå Email kh√¥ng h·ª£p l·ªá", "error");
      return;
    }

    setLoading(true);
    try {
      const response = await api.post("/auth/forgot-password", { email });
      console.log("Forgot password response:", response.data);

      setSent(true);
      addToast(
        "‚úÖ " + (response.data.message || "ƒê√£ g·ª≠i email h∆∞·ªõng d·∫´n!"),
        "success"
      );

      // For development testing
      if (response.data.resetToken) {
        setDevToken(response.data.resetToken);
        if (response.data.resetUrl) {
          console.log("üîó Reset URL (dev):", response.data.resetUrl);
        }
      }

      setEmail("");
    } catch (err) {
      console.error("Forgot password error:", err);
      addToast(
        err.response?.data?.message || "‚ùå L·ªói khi g·ª≠i email ƒë·∫∑t l·∫°i m·∫≠t kh·∫©u",
        "error"
      );
    } finally {
      setLoading(false);
    }
  };

  if (sent) {
    return (
      <div style={styles.container}>
        <div style={styles.card}>
          <div style={styles.successIcon}>üìß</div>
          <h2 style={styles.title}>Email ƒê√£ ƒê∆∞·ª£c G·ª≠i!</h2>
          <p style={styles.description}>
            Ch√∫ng t√¥i ƒë√£ g·ª≠i h∆∞·ªõng d·∫´n ƒë·∫∑t l·∫°i m·∫≠t kh·∫©u ƒë·∫øn email c·ªßa b·∫°n.
          </p>
          <p style={styles.hint}>
            Vui l√≤ng ki·ªÉm tra h·ªôp th∆∞ (v√† c·∫£ th∆∞ m·ª•c spam) c·ªßa b·∫°n.
          </p>
          <div style={styles.actionRow}>
            <button
              type="button"
              className="btn secondary"
              onClick={() => setSent(false)}
              style={{ marginRight: 8 }}
            >
              G·ª≠i l·∫°i
            </button>
            <a href="/login" className="btn">
              Quay l·∫°i ƒëƒÉng nh·∫≠p
            </a>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.iconWrapper}>
          <div style={styles.icon}>üîê</div>
        </div>

        <h2 style={styles.title}>Qu√™n M·∫≠t Kh·∫©u?</h2>
        <p style={styles.description}>
          Nh·∫≠p email c·ªßa b·∫°n v√† ch√∫ng t√¥i s·∫Ω g·ª≠i h∆∞·ªõng d·∫´n ƒë·∫∑t l·∫°i m·∫≠t kh·∫©u.
        </p>

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="your.email@example.com"
              disabled={loading}
              style={styles.input}
              required
            />
          </div>

          <button
            type="submit"
            className="btn"
            disabled={loading}
            style={{ width: "100%", marginTop: 8 }}
          >
            {loading ? "‚è≥ ƒêang g·ª≠i..." : "üìß G·ª≠i Email ƒê·∫∑t L·∫°i M·∫≠t Kh·∫©u"}
          </button>
        </form>

        {devToken && (
          <div style={{ marginTop: 12 }}>
            <p style={{ fontSize: 13 }}>DEV reset token (d√πng ƒë·ªÉ test reset):</p>
            <pre
              style={{
                background: "#f3f4f6",
                padding: 8,
                borderRadius: 6,
                overflowX: "auto",
              }}
            >
              {devToken}
            </pre>
          </div>
        )}

        <div style={styles.footer}>
          <a href="/login" style={styles.link}>
            ‚Üê Quay l·∫°i ƒëƒÉng nh·∫≠p
          </a>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
    padding: "20px",
  },
  card: {
    background: "white",
    borderRadius: "12px",
    boxShadow: "0 10px 40px rgba(0,0,0,0.2)",
    padding: "40px",
    maxWidth: "450px",
    width: "100%",
  },
  iconWrapper: {
    textAlign: "center",
    marginBottom: "20px",
  },
  icon: {
    fontSize: "64px",
    display: "inline-block",
    animation: "pulse 2s infinite",
  },
  successIcon: {
    fontSize: "64px",
    textAlign: "center",
    marginBottom: "20px",
  },
  title: {
    fontSize: "28px",
    fontWeight: "bold",
    color: "#1f2937",
    textAlign: "center",
    marginBottom: "12px",
  },
  description: {
    fontSize: "15px",
    color: "#6b7280",
    textAlign: "center",
    marginBottom: "24px",
    lineHeight: 1.6,
  },
  hint: {
    fontSize: "14px",
    color: "#9ca3af",
    textAlign: "center",
    marginBottom: "24px",
  },
  form: {
    marginTop: "24px",
  },
  inputGroup: {
    marginBottom: "16px",
  },
  label: {
    display: "block",
    fontSize: "14px",
    fontWeight: "600",
    color: "#374151",
    marginBottom: "8px",
  },
  input: {
    width: "100%",
    padding: "12px 16px",
    border: "2px solid #e5e7eb",
    borderRadius: "8px",
    fontSize: "15px",
    transition: "all 0.3s",
    boxSizing: "border-box",
  },
  footer: {
    marginTop: "24px",
    textAlign: "center",
  },
  link: {
    color: "#667eea",
    textDecoration: "none",
    fontSize: "14px",
    fontWeight: "500",
  },
  actionRow: {
    display: "flex",
    gap: "8px",
    justifyContent: "center",
  },
};
