import React, { useEffect, useState } from "react";
import api from "../../services/api";
import { useAuth } from "../../contexts/AuthContext";

export default function AdminUserList() {
  const { user: me } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState({ name: "", email: "" });

  const fetchUsers = async () => {
    try {
      const res = await api.get("/users");
      setUsers(res.data);
    } catch (err) {
      console.error("Failed to fetch users:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async (id) => {
    if (!confirm("Bạn có chắc muốn xóa user này?")) return;
    try {
      await api.delete(`/users/${id}`);
      setUsers((s) => s.filter((u) => u._id !== id));
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "Xóa thất bại");
    }
  };

  const startEdit = (u) => {
    setEditingId(u._id);
    setEditData({ name: u.name, email: u.email });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditData({ name: "", email: "" });
  };

  const saveEdit = async (id) => {
    try {
      const res = await api.put(`/users/${id}`, editData);
      setUsers((s) => s.map((u) => (u._id === id ? res.data.user : u)));
      cancelEdit();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "Cập nhật thất bại");
    }
  };

  return (
    <div className="app-container">
      <div className="site-header">
        <h1 className="site-title">👑 Trang quản trị</h1>
        <div style={{ textAlign: "right" }}>
          <div style={{ fontSize: 14, fontWeight: 700 }}>{me?.name || "Admin"}</div>
          <div style={{ fontSize: 12, color: "#6b7280" }}>{me?.email}</div>
        </div>
      </div>

      <div className="card">
        <h3>Danh sách người dùng</h3>

        {loading ? (
          <p className="muted">Đang tải...</p>
        ) : (
          <table className="user-table">
            <thead>
              <tr>
                <th style={{ width: 220 }}>ID</th>
                <th>Tên</th>
                <th>Email</th>
                <th style={{ width: 220 }}>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {users.map((u) => (
                <tr key={u._id}>
                  <td style={{ wordBreak: "break-all" }}>{u._id}</td>
                  <td>
                    {editingId === u._id ? (
                      <input value={editData.name} onChange={(e) => setEditData({ ...editData, name: e.target.value })} />
                    ) : (
                      u.name
                    )}
                  </td>
                  <td>
                    {editingId === u._id ? (
                      <input value={editData.email} onChange={(e) => setEditData({ ...editData, email: e.target.value })} />
                    ) : (
                      u.email
                    )}
                  </td>
                  <td>
                    {editingId === u._id ? (
                      <>
                        <button className="btn secondary" onClick={() => saveEdit(u._id)} style={{ marginRight: 8 }}>Lưu</button>
                        <button className="btn ghost" onClick={cancelEdit}>Hủy</button>
                      </>
                    ) : (
                      <>
                        <button className="btn secondary" onClick={() => startEdit(u)} style={{ marginRight: 8 }}>Sửa</button>
                        <button className="btn ghost" onClick={() => handleDelete(u._id)}>Xóa</button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
