import React, { useState } from "react";
import api from "../../services/api";
import { useToast } from "../../contexts/ToastContext";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";

const RegisterForm = () => {
  const [form, setForm] = useState({ name: "", email: "", password: "" });
  const [confirm, setConfirm] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();
  const { addToast } = useToast();
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!form.password || form.password.length < 6) {
      setMessage("M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±");
      return;
    }

    if (form.password !== confirm) {
      setMessage("M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp");
      return;
    }

    try {
      // ƒêƒÉng k√Ω
      await api.post("/auth/register", form);
      addToast("ƒêƒÉng k√Ω th√†nh c√¥ng!", 'success');

      // T·ª± ƒë·ªông ƒëƒÉng nh·∫≠p sau khi ƒëƒÉng k√Ω
      const loginRes = await api.post("/auth/login", {
        email: form.email,
        password: form.password
      });

      const { accessToken, refreshToken, user } = loginRes.data;
      login(accessToken, refreshToken, user);

      addToast("ƒê√£ ƒëƒÉng nh·∫≠p!", 'success');
      navigate("/profile");
    } catch (err) {
      console.error(err);
      addToast(err.response?.data?.message || "L·ªói khi ƒëƒÉng k√Ω", 'error');
    }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card">
        <h2 className="auth-title">üìù ƒêƒÉng k√Ω</h2>
        <p className="auth-sub">T·∫°o t√†i kho·∫£n m·ªõi ƒë·ªÉ truy c·∫≠p c√°c t√≠nh nƒÉng</p>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>T√™n</label>
            <input type="text" placeholder="T√™n" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
          </div>

          <div className="form-group">
            <label>Email</label>
            <input type="email" placeholder="Email" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} />
          </div>

          <div className="form-group">
            <label>M·∫≠t kh·∫©u</label>
            <input type="password" placeholder="M·∫≠t kh·∫©u" value={form.password} onChange={(e) => setForm({ ...form, password: e.target.value })} />
          </div>

          <div className="form-group">
            <label>X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
            <input type="password" placeholder="Nh·∫≠p l·∫°i m·∫≠t kh·∫©u" value={confirm} onChange={(e) => setConfirm(e.target.value)} />
          </div>

          {message && <p className="status-message">{message}</p>}

          <div className="center">
            <button className="btn" type="submit">ƒêƒÉng k√Ω</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default RegisterForm;
