import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import { useToast } from "../../contexts/ToastContext";
import api from "../../services/api"; // axios c√≥ baseURL: http://localhost:5000/api

export default function LoginForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();
  const { addToast } = useToast();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // üü¢ G·ªçi API ƒëƒÉng nh·∫≠p
      const res = await api.post("/auth/login", { email, password });
      const { token, user } = res.data;

      // ‚úÖ L∆∞u token v√† userId v√†o localStorage
      localStorage.setItem("token", token);
      localStorage.setItem("userId", user._id || user.id);

      // N·∫øu d√πng Context th√¨ v·∫´n gi·ªØ l·∫°i
      login(token, user);

      addToast("ƒêƒÉng nh·∫≠p th√†nh c√¥ng!", 'success');
      // N·∫øu l√† admin chuy·ªÉn t·ªõi trang admin
      if (user.role === "admin") {
        navigate("/admin");
      } else {
        navigate("/profile");
      }
    } catch (err) {
      console.error("‚ùå L·ªói ƒëƒÉng nh·∫≠p:", err);
      addToast(err.response?.data?.message || "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i", 'error');
    }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card">
        <h2 className="auth-title">ƒêƒÉng nh·∫≠p</h2>
        <p className="auth-sub">ƒêƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c ƒë·∫øn trang c√° nh√¢n</p>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} required />
          </div>

          <div className="form-group">
            <label>M·∫≠t kh·∫©u</label>
            <input type="password" placeholder="M·∫≠t kh·∫©u" onChange={(e) => setPassword(e.target.value)} required />
          </div>

          <div className="center">
            <button className="btn" type="submit">ƒêƒÉng nh·∫≠p</button>
          </div>
        </form>

        {/* toasts handle messages */}
      </div>
    </div>
  );
}
