import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../services/api";
import { useToast } from "../../contexts/ToastContext";
import { useAuth } from "../../contexts/AuthContext";
import AvatarUpload from "./AvatarUpload";
import "../../App.css";

function ProfilePage() {
  const [user, setUser] = useState({ name: "", email: "" });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");

  const navigate = useNavigate();
  const { logout } = useAuth();
  const { addToast } = useToast();

  // üü¢ L·∫•y th√¥ng tin ng∆∞·ªùi d√πng hi·ªán t·∫°i
  const fetchProfile = async () => {
    try {
      const res = await api.get(`/profile`);
      setUser(res.data.user || res.data);
      setLoading(false);
    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i th√¥ng tin c√° nh√¢n:", err);
      setMessage("‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin c√° nh√¢n!");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  // üü£ C·∫≠p nh·∫≠t th√¥ng tin c√° nh√¢n
  const handleUpdate = async (e) => {
    e.preventDefault();

    if (newPassword) {
      if (newPassword.length < 6) {
        addToast("M·∫≠t kh·∫©u m·ªõi ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±", "warning");
        return;
      }
      if (newPassword !== confirmNewPassword) {
        addToast("M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp", "warning");
        return;
      }
    }

    try {
      const payload = { name: user.name, email: user.email };
      if (newPassword) payload.password = newPassword;
      await api.put("/profile", payload);

      addToast("C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng", "success");
      setNewPassword("");
      setConfirmNewPassword("");
      fetchProfile();
    } catch (err) {
      console.error("‚ùå L·ªói khi c·∫≠p nh·∫≠t:", err);
      addToast(err.response?.data?.message || "C·∫≠p nh·∫≠t th·∫•t b·∫°i", "error");
    }
  };

  // üü• X√≥a t√†i kho·∫£n (ng∆∞·ªùi d√πng t·ª± x√≥a)
  const handleDeleteAccount = async () => {
    const ok = window.confirm(
      "B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t√†i kho·∫£n c·ªßa m√¨nh? H√†nh ƒë·ªông n√†y kh√¥ng th·ªÉ ho√†n t√°c."
    );
    if (!ok) return;

    try {
      await api.delete("/profile");
      addToast("T√†i kho·∫£n ƒë√£ ƒë∆∞·ª£c x√≥a", "success");
      logout();
      localStorage.removeItem("userId");
      navigate("/");
    } catch (err) {
      console.error("‚ùå L·ªói khi x√≥a t√†i kho·∫£n:", err);
      addToast(err.response?.data?.message || "X√≥a th·∫•t b·∫°i", "error");
    }
  };

  // üö™ ƒêƒÉng xu·∫•t t√†i kho·∫£n
  const handleLogout = () => {
    logout();
    addToast("ƒêƒÉng xu·∫•t th√†nh c√¥ng", "info");
    navigate("/login");
  };

  if (loading) return <p className="loading">‚è≥ ƒêang t·∫£i th√¥ng tin...</p>;

  return (
    <div className="auth-wrap">
      <div className="auth-card">
<div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <div>
            <h2 className="auth-title">üë§ Th√¥ng tin c√° nh√¢n</h2>
            <p className="auth-sub">Qu·∫£n l√Ω th√¥ng tin c√° nh√¢n v√† m·∫≠t kh·∫©u</p>
          </div>

          <div style={{ textAlign: "right" }}>
            <div
              style={{
                fontSize: 13,
                fontWeight: 700,
                color: user.role === "admin" ? "#991B1B" : "#0f172a",
              }}
            >
              {user.role === "admin" ? "Admin" : "Ng∆∞·ªùi d√πng"}
            </div>
            {user.role === "admin" && (
              <a
                href="/admin"
                style={{ fontSize: 12, color: "#4f46e5", display: "block" }}
              >
                ƒêi t·ªõi trang qu·∫£n tr·ªã
              </a>
            )}

            {/* N√∫t ƒëƒÉng xu·∫•t */}
            <button
              onClick={handleLogout}
              style={{
                marginTop: 6,
                background: "#e0233dff",
                color: "white",
                border: "none",
                padding: "6px 10px",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: 12,
              }}
            >
              ƒêƒÉng xu·∫•t
            </button>
          </div>
        </div>

        <AvatarUpload currentAvatar={user.avatar} onUploaded={fetchProfile} />

        <form onSubmit={handleUpdate}>
          <div className="form-group">
            <label>H·ªç v√† t√™n:</label>
            <input
              type="text"
              value={user.name}
              onChange={(e) => setUser({ ...user, name: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              value={user.email}
              onChange={(e) => setUser({ ...user, email: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label>M·∫≠t kh·∫©u m·ªõi (t√πy ch·ªçn):</label>
            <input
              type="password"
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi n·∫øu mu·ªën ƒë·ªïi"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
          </div>

          <div className="form-group">
            <label>X√°c nh·∫≠n m·∫≠t kh·∫©u m·ªõi</label>
            <input
              type="password"
              placeholder="Nh·∫≠p l·∫°i m·∫≠t kh·∫©u m·ªõi"
              value={confirmNewPassword}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
            />
          </div>

          <div
            className="center"
            style={{
              display: "flex",
              gap: 12,
justifyContent: "center",
              alignItems: "center",
            }}
          >
            <button className="btn" type="submit">
              üíæ L∆∞u thay ƒë·ªïi
            </button>
            <button
              type="button"
              onClick={handleDeleteAccount}
              className="btn"
              style={{ background: "#ef4444", borderColor: "#ef4444" }}
            >
              üóëÔ∏è X√≥a t√†i kho·∫£n
            </button>
          </div>
        </form>

        {message && <p className="status-message">{message}</p>}
      </div>
    </div>
  );
}

export default ProfilePage;
