import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import api from "../../services/api"; // axios c√≥ baseURL: http://localhost:5000/api

export default function LoginForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // üü¢ G·ªçi API ƒëƒÉng nh·∫≠p
      const res = await api.post("/auth/login", { email, password });
      const { token, user } = res.data;

      // ‚úÖ L∆∞u token v√† userId v√†o localStorage
      localStorage.setItem("token", token);
      localStorage.setItem("userId", user._id || user.id);

      // N·∫øu d√πng Context th√¨ v·∫´n gi·ªØ l·∫°i
      login(token, user);

      setMessage("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
      navigate("/profile");
    } catch (err) {
      console.error("‚ùå L·ªói ƒëƒÉng nh·∫≠p:", err);
      setMessage(err.response?.data?.message || "‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!");
    }
  };

  return (
    <div className="auth-container">
      <h2>ƒêƒÉng nh·∫≠p</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u"
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">ƒêƒÉng nh·∫≠p</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}
