import React, { useState, useEffect } from "react";
import axios from "axios";
import "../../App.css";

function ProfilePage() {
  const [user, setUser] = useState({ name: "", email: "" });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const token = localStorage.getItem("token");
  const userId = localStorage.getItem("userId"); // üÜï L∆∞u khi login xong

  // üü¢ L·∫•y th√¥ng tin ng∆∞·ªùi d√πng hi·ªán t·∫°i
  const fetchProfile = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/profile?id=${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(res.data.user);
      setLoading(false);
    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i th√¥ng tin c√° nh√¢n:", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  // üü£ C·∫≠p nh·∫≠t th√¥ng tin c√° nh√¢n
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        "http://localhost:5000/api/profile",
        { id: userId, name: user.name, email: user.email, password: newPassword },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage("‚úÖ C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng!");
      setNewPassword("");
      fetchProfile();
    } catch (err) {
      console.error("‚ùå L·ªói khi c·∫≠p nh·∫≠t:", err);
      setMessage("‚ùå C·∫≠p nh·∫≠t th·∫•t b·∫°i!");
    }
  };

  if (loading) return <p className="loading">‚è≥ ƒêang t·∫£i th√¥ng tin...</p>;

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h2 className="profile-title">üë§ Th√¥ng tin c√° nh√¢n</h2>

        <form onSubmit={handleUpdate} className="profile-form">
          <div className="form-group">
            <label>H·ªç v√† t√™n:</label>
            <input
              type="text"
              value={user.name}
              onChange={(e) => setUser({ ...user, name: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              value={user.email}
              onChange={(e) => setUser({ ...user, email: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label>M·∫≠t kh·∫©u m·ªõi (t√πy ch·ªçn):</label>
            <input
              type="password"
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi n·∫øu mu·ªën ƒë·ªïi"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
          </div>

          <button type="submit" className="btn-update">
            üíæ L∆∞u thay ƒë·ªïi
          </button>
        </form>

        {message && <p className="status-message">{message}</p>}
      </div>
    </div>
  );
}

export default ProfilePage;
